import React from "react";
import './Style/card.css'
import { data } from "./cardsdata";

const Card = () => {
    return(
        <>
            <div className="maincard">
                <div className="topcard">
                    <img className="Cards-Image" src={data[0].imagesrc} alt="" />
                </div>
                <div className="bottomcard">
                    <h1 className="text-black">{data[0].title}</h1>
                    <span className="text-black h6">{data[0].description}</span>
                    <a target="_blank" href={data[0].anchor}>
                        <span className="text-black h6">{data[0].anchor}</span>
                    </a>
                </div>
            </div>
        </>
    )
}

export { Card }
